:root {
  --bg: #0a0f17;
  --panel: #111825;
  --text: #e8edf7;
  --muted: #9aa7bd;
  --border: #263249;
  --accent: #5cc8ff;
  --ok: #43d58c;
  --warn: #ffd166;
  --err: #ff6b6b;
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  font-family: ui-sans-serif, system-ui, -apple-system, "Segoe UI", sans-serif;
  color: var(--text);
  background: radial-gradient(circle at 0 0, #17243a, var(--bg) 36%);
}

button,
select {
  min-height: 44px;
}

button,
select {
  background: #111b2b;
  color: var(--text);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 0.45rem 0.6rem;
}

button {
  cursor: pointer;
}

button:hover {
  border-color: #3a4d72;
}

button:focus-visible,
select:focus-visible,
.event-summary:focus-visible,
.tab:focus-visible {
  outline: 2px solid var(--accent);
  outline-offset: 2px;
}

.skip-link {
  position: absolute;
  left: 0.75rem;
  top: -100px;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 0.55rem 0.75rem;
  color: var(--text);
  z-index: 100;
}

.skip-link:focus {
  top: 0.75rem;
}

.topbar {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem 1rem;
  border-bottom: 1px solid var(--border);
  background: rgba(10, 15, 23, 0.78);
  position: sticky;
  top: 0;
  z-index: 10;
  backdrop-filter: blur(8px);
}

.topbar-main {
  display: flex;
  flex-direction: column;
  gap: 0.35rem;
}

.topbar h1 {
  margin: 0;
  font-size: 1.08rem;
}

.run-strip {
  display: flex;
  flex-wrap: wrap;
  gap: 0.45rem;
  align-items: center;
}

.controls {
  display: flex;
  flex-wrap: wrap;
  gap: 0.55rem;
  align-items: end;
}

label {
  display: flex;
  flex-direction: column;
  gap: 0.2rem;
  color: var(--muted);
  font-size: 0.75rem;
}

.phase-panel {
  margin: 0.75rem;
  border: 1px solid var(--border);
  border-radius: 12px;
  background: var(--panel);
  padding: 0.75rem;
}

.phase-head {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 0.75rem;
  flex-wrap: wrap;
}

.phase-head h2 {
  margin: 0;
  font-size: 0.94rem;
}

.phase-list {
  margin: 0.55rem 0 0;
  padding: 0;
  list-style: none;
  display: flex;
  gap: 0.45rem;
  overflow-x: auto;
}

.phase-item {
  border: 1px solid var(--border);
  border-radius: 999px;
  padding: 0.35rem 0.6rem;
  font-size: 0.74rem;
  white-space: nowrap;
}

.phase-item.active {
  border-color: var(--accent);
  color: var(--accent);
}

.phase-item.complete {
  border-color: var(--ok);
  color: var(--ok);
}

.phase-item.failed {
  border-color: var(--err);
  color: var(--err);
}

.layout {
  display: grid;
  gap: 0.75rem;
  grid-template-columns: 42% 58%;
  grid-template-areas:
    "timeline context"
    "timeline secondary";
  padding: 0 0.75rem 0.75rem;
  min-height: calc(100vh - 210px);
}

.panel {
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 0.8rem;
  min-height: 0;
  display: flex;
  flex-direction: column;
}

.timeline-pane {
  grid-area: timeline;
}

.context-pane {
  grid-area: context;
}

.secondary-pane {
  grid-area: secondary;
}

.panel-head {
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 0.75rem;
}

.panel h2,
.panel h3 {
  margin: 0;
}

.panel h2 {
  font-size: 0.95rem;
}

.panel h3 {
  font-size: 0.84rem;
  margin-bottom: 0.35rem;
}

.meta {
  color: var(--muted);
  font-size: 0.78rem;
}

.meta-pill,
.badge {
  border-radius: 999px;
  border: 1px solid var(--border);
  padding: 0.14rem 0.5rem;
  font-size: 0.72rem;
}

.badge.running,
.badge.executing {
  color: var(--warn);
}

.badge.completed {
  color: var(--ok);
}

.badge.failed {
  color: var(--err);
}

.timeline-actions {
  margin: 0.6rem 0;
  display: flex;
  gap: 0.55rem;
  flex-wrap: wrap;
}

.notice {
  margin: 0 0 0.6rem;
  color: var(--warn);
  font-size: 0.76rem;
}

.events {
  list-style: none;
  margin: 0;
  padding: 0;
  border: 1px solid var(--border);
  border-radius: 10px;
  background: #0d1522;
  overflow: auto;
  min-height: 300px;
  max-height: min(62vh, 720px);
}

.event-row {
  border-bottom: 1px solid #1b2740;
}

.event-row:last-child {
  border-bottom: 0;
}

.event-row[aria-selected="true"] {
  background: rgba(92, 200, 255, 0.12);
}

.event-summary {
  width: 100%;
  text-align: left;
  background: transparent;
  border: 0;
  border-radius: 0;
  padding: 0.5rem 0.55rem;
  min-height: 44px;
}

.event-head {
  display: flex;
  justify-content: space-between;
  gap: 0.45rem;
  font-size: 0.76rem;
  margin-bottom: 0.25rem;
}

.event-type {
  color: var(--accent);
}

.event-preview {
  color: var(--text);
  font-size: 0.75rem;
  line-height: 1.35;
}

.event-tools {
  display: flex;
  justify-content: flex-end;
  padding: 0 0.55rem 0.5rem;
}

.payload-toggle {
  padding: 0.25rem 0.45rem;
  font-size: 0.72rem;
  min-height: 32px;
}

.payload {
  margin: 0 0.55rem 0.55rem;
  border: 1px solid var(--border);
  border-radius: 10px;
  background: #0b1420;
  padding: 0.55rem;
  white-space: pre-wrap;
  font-size: 0.72rem;
  color: #d5def0;
  overflow-x: auto;
}

