125→      id: `#${issue.number}`,
126→      source: "github",
127→      title: issue.title,
128→      description: issue.body ?? "",
129→      labels: issue.labels?.map((l: any) => l.name) ?? [],
130→      priority: this.inferGitHubPriority(issue.labels),
131→      acceptance_criteria: this.extractAcceptanceCriteria(issue.body ?? ""),
132→      linked_tickets: [],
133→      comments: comments.map((c: any) => c.body),
134→      author: issue.user?.login ?? "unknown",
135→      assignee: issue.assignee?.login,
136→      raw: issue,
137→    };
138→  }
139→
140→  private async fetchJira(ticketId: string): Promise<TicketDetails> {
141→    const config = this.integrations.ticket_source.config;
142→    const host = config.host;
143→    const email = config.email;
144→    const apiToken = config.api_token;
145→    if (!host || !email || !apiToken) {
146→      throw new Error("Jira integration not fully configured (need host, email, api_token)");
147→    }
148→
149→    const auth = Buffer.from(`${email}:${apiToken}`).toString("base64");
150→
151→    const resp = await fetch(`${host}/rest/api/3/issue/${ticketId}`, {
152→      headers: {
153→        Authorization: `Basic ${auth}`,
154→        Accept: "application/json",
155→      },
156→    });
157→
158→    if (!resp.ok) {
159→      throw new Error(`Jira API error: ${resp.status} ${resp.statusText}`);
160→    }
161→
162→    const issue = (await resp.json()) as any;
163→    const fields = issue.fields;
164→
165→    return {
166→      id: issue.key,
167→      source: "jira",
168→      title: fields.summary,
169→      description: this.extractJiraDescription(fields.description),
170→      labels: fields.labels ?? [],
171→      priority: this.mapJiraPriority(fields.priority?.name),
172→      acceptance_criteria: this.extractAcceptanceCriteria(
173→        this.extractJiraDescription(fields.description)
174→      ),
175→      linked_tickets: fields.issuelinks?.map((l: any) =>
176→        l.outwardIssue?.key ?? l.inwardIssue?.key
177→      ).filter(Boolean) ?? [],
178→      comments: fields.comment?.comments?.map((c: any) =>
179→        this.extractJiraDescription(c.body)
180→      ) ?? [],
181→      author: fields.creator?.displayName ?? "unknown",
182→      assignee: fields.assignee?.displayName,
183→      raw: issue,
184→    };
185→  }
186→
187→  // ---- Helpers ----
188→
189→  private mapLinearPriority(priority: number): TicketDetails["priority"] {
190→    // Linear: 0=no priority, 1=urgent, 2=high, 3=medium, 4=low
191→    if (priority <= 1) return "p0";
192→    if (priority === 2) return "p1";
193→    if (priority === 3) return "p2";
194→    return "p3";
195→  }
196→
197→  private inferGitHubPriority(
198→    labels: { name: string }[]
199→  ): TicketDetails["priority"] {
200→    const names = labels.map((l) => l.name.toLowerCase());
201→    if (names.some((n) => n.includes("critical") || n.includes("p0"))) return "p0";
202→    if (names.some((n) => n.includes("high") || n.includes("p1"))) return "p1";
203→    if (names.some((n) => n.includes("low") || n.includes("p3"))) return "p3";
204→    return "p2";
205→  }
206→
207→  private mapJiraPriority(name?: string): TicketDetails["priority"] {
208→    const lower = (name ?? "").toLowerCase();
209→    if (lower.includes("highest") || lower.includes("critical")) return "p0";
210→    if (lower.includes("high")) return "p1";
211→    if (lower.includes("low") || lower.includes("lowest")) return "p3";
212→    return "p2";
213→  }
214→
215→  private extractAcceptanceCriteria(description: string): string[] {
216→    const criteria: string[] = [];
217→    // Look for common AC patterns: "- [ ] ...", "AC:", "Acceptance Criteria:", numbered lists after AC header
218→    const lines = description.split("\n");
219→    let inAC = false;
220→
221→    for (const line of lines) {
222→      const trimmed = line.trim();
223→      if (/acceptance\s*criteria/i.test(trimmed)) {
224→        inAC = true;
225→        continue;
226→      }
227→      if (inAC) {
228→        if (trimmed.startsWith("- ") || trimmed.startsWith("* ") || /^\d+\./.test(trimmed)) {
229→          criteria.push(trimmed.replace(/^[-*\d.[\]\s]+/, "").trim());
230→        } else if (trimmed === "" && criteria.length > 0) {
231→          break; // End of AC section
232→        }
233→      }
234→      // Also capture checkbox items anywhere
235→      if (/^-\s*\[[ x]\]\s*/.test(trimmed)) {
236→        const item = trimmed.replace(/^-\s*\[[ x]\]\s*/, "").trim();
237→        if (!criteria.includes(item)) criteria.push(item);
238→      }
239→    }
240→
241→    return criteria;
242→  }
243→
244→  private extractJiraDescription(adf: any): string {
245→    // Jira uses Atlassian Document Format — extract plain text
246→    if (typeof adf === "string") return adf;
247→    if (!adf?.content) return "";
248→
249→    const extract = (node: any): string => {
250→      if (node.type === "text") return node.text ?? "";
251→      if (node.content) return node.content.map(extract).join("");
252→      return "";
253→    };
254→
255→    return adf.content.map(extract).join("\n");
256→  }
257→}
