  1→import http from "node:http";
  2→import os from "node:os";
  3→import path from "node:path";
  4→import { fileURLToPath } from "node:url";
  5→import { promises as fs } from "node:fs";
  6→
  7→const __filename = fileURLToPath(import.meta.url);
  8→const __dirname = path.dirname(__filename);
  9→const publicDir = path.join(__dirname, "public");
 10→const publicV2Dir = path.join(__dirname, "public-v2");
 11→const publicV3Dir = path.join(__dirname, "public-v3");
 12→const runsRoot = process.env.AGENTSDLC_RUNS_ROOT ?? path.join(os.tmpdir(), "agentsdlc");
 13→const port = Number(process.env.MONITOR_PORT ?? 4310);
 14→
 15→const LOG_KIND_TO_FILES = {
 16→  planner_stdout: [".planner-runtime.stdout.log"],
 17→  planner_stderr: [".planner-runtime.stderr.log"],
 18→  // Prefer whichever runtime wrote most recently for agent logs.
 19→  agent_stdout: [".codex-runtime.stdout.log", ".claude-runtime.stdout.log"],
 20→  agent_stderr: [".codex-runtime.stderr.log", ".claude-runtime.stderr.log"],
 21→  agent_result: [".agent-result.json"],
 22→};
 23→
 24→function sendJson(res, status, body) {
 25→  res.writeHead(status, { "Content-Type": "application/json; charset=utf-8" });
 26→  res.end(JSON.stringify(body));
 27→}
 28→
 29→function sendText(res, status, body) {
 30→  res.writeHead(status, { "Content-Type": "text/plain; charset=utf-8" });
 31→  res.end(body);
 32→}
 33→
 34→function parseJsonLines(raw) {
 35→  return raw
 36→    .split("\n")
 37→    .map((line) => line.trim())
 38→    .filter(Boolean)
 39→    .map((line) => {
 40→      try {
 41→        return JSON.parse(line);
 42→      } catch {
 43→        return null;
 44→      }
 45→    })
 46→    .filter(Boolean);
 47→}
 48→
 49→function safeJoin(base, ...parts) {
 50→  const resolved = path.resolve(base, ...parts);
 51→  const resolvedBase = path.resolve(base);
 52→  if (!resolved.startsWith(resolvedBase + path.sep) && resolved !== resolvedBase) {
 53→    throw new Error("Invalid path traversal attempt");
 54→  }
 55→  return resolved;
 56→}
 57→
 58→async function listRuns() {
 59→  const projects = await fs.readdir(runsRoot, { withFileTypes: true }).catch(() => []);
 60→  const runs = [];
 61→
 62→  for (const projectDir of projects) {
 63→    if (!projectDir.isDirectory()) continue;
 64→    const projectId = projectDir.name;
 65→    const projectPath = path.join(runsRoot, projectId);
 66→    const runDirs = await fs.readdir(projectPath, { withFileTypes: true }).catch(() => []);
 67→
 68→    for (const runDir of runDirs) {
 69→      if (!runDir.isDirectory()) continue;
 70→      const runId = runDir.name;
 71→      if (!runId.startsWith("run-")) continue;
 72→      const runPath = path.join(projectPath, runId);
 73→      const summary = await loadRunSummary(projectId, runId, runPath);
 74→      runs.push(summary);
 75→    }
 76→  }
 77→
 78→  runs.sort((a, b) => (b.last_event_ts ?? 0) - (a.last_event_ts ?? 0));
 79→  return runs;
 80→}
 81→
 82→async function readEvents(runPath) {
 83→  const eventsPath = path.join(runPath, ".events.jsonl");
 84→  const raw = await fs.readFile(eventsPath, "utf-8").catch(() => "");
 85→  return parseJsonLines(raw);
 86→}
 87→
 88→function inferStatus(events) {
 89→  for (let i = events.length - 1; i >= 0; i--) {
 90→    const t = events[i]?.event_type;
 91→    if (t === "task.completed") return "completed";
 92→    if (t === "task.failed") return "failed";
 93→    if (t === "step.started") return "executing";
 94→    if (t === "task.plan_generated") return "planning";
 95→    if (t === "task.created") return "pending";
 96→  }
 97→  return "unknown";
 98→}
 99→
100→function buildStepStatus(plan, events) {
101→  const byStep = new Map();
102→  for (const step of plan?.steps ?? []) {
103→    byStep.set(step.step_number, {
104→      step_number: step.step_number,
105→      agent: step.agent,
106→      task: step.task,
107→      status: "pending",
108→      started_at: null,
109→      completed_at: null,
110→      tokens: null,
111→    });
112→  }
113→
114→  for (const evt of events) {
115→    const data = evt?.data ?? {};
116→    const stepNum = data.step;
117→    if (typeof stepNum !== "number") continue;
118→    if (!byStep.has(stepNum)) continue;
119→    const st = byStep.get(stepNum);
120→    if (evt.event_type === "step.started") {
121→      st.status = "running";
122→      st.started_at = evt.timestamp ?? null;
123→    } else if (evt.event_type === "step.completed") {
124→      st.status = "completed";
125→      st.completed_at = evt.timestamp ?? null;
126→      st.tokens = data.tokens ?? null;
127→    } else if (evt.event_type === "step.failed") {
128→      st.status = "failed";
129→      st.completed_at = evt.timestamp ?? null;
130→    }
131→  }
132→
133→  return Array.from(byStep.values()).sort((a, b) => a.step_number - b.step_number);
134→}
135→
136→async function loadRunSummary(projectId, runId, runPath) {
137→  const events = await readEvents(runPath);
138→  const planEvent = events.find((e) => e.event_type === "task.plan_generated");
139→  const plan = planEvent?.data?.plan ?? null;
140→  const steps = buildStepStatus(plan, events);
141→  const last = events.at(-1);
142→  return {
143→    project_id: projectId,
144→    run_id: runId,
145→    status: inferStatus(events),
146→    classification: plan?.classification ?? null,
147→    step_count: plan?.steps?.length ?? 0,
148→    steps,
149→    started_at: events.find((e) => e.event_type === "task.created")?.timestamp ?? null,
150→    last_event_type: last?.event_type ?? null,
151→    last_event_ts: last?.timestamp ? Date.parse(last.timestamp) : null,
152→    workspace_path: runPath,
153→  };
154→}
155→
156→async function loadRun(projectId, runId) {
157→  const runPath = safeJoin(runsRoot, projectId, runId);
158→  const summary = await loadRunSummary(projectId, runId, runPath);
159→  const events = await readEvents(runPath);
160→  const planEvent = events.find((e) => e.event_type === "task.plan_generated");
161→  const step_models = await loadStepModels(runPath);
162→  return {
163→    ...summary,
164→    plan: planEvent?.data?.plan ?? null,
165→    step_models,
166→  };
167→}
168→
169→function extractModelNameFromDebug(payload) {
170→  if (!payload || typeof payload !== "object") return "";
171→  return (
172→    payload.model ||
173→    payload.openai_model ||
174→    payload.anthropic_model ||
175→    payload.google_model ||
176→    payload.selected_model ||
177→    payload.model_name ||
178→    ""
179→  );
180→}
181→
182→async function loadStepModels(runPath) {
183→  const entries = await fs.readdir(runPath, { withFileTypes: true }).catch(() => []);
184→  const byStep = new Map();
185→  const stepPattern = /\.(?:codex|claude)-runtime\.step-(\d+)\.attempt-(\d+)\.debug\.json$/;
186→  const genericPattern = /\.(?:codex|claude)-runtime\.debug\.json$/;
187→
188→  for (const entry of entries) {
189→    if (!entry.isFile()) continue;
190→    const filePath = path.join(runPath, entry.name);
191→    const raw = await fs.readFile(filePath, "utf-8").catch(() => "");
192→    if (!raw) continue;
193→    let parsed = null;
194→    try {
195→      parsed = JSON.parse(raw);
196→    } catch {
197→      parsed = null;
198→    }
199→    if (!parsed) continue;
200→
201→    let stepNumber = null;
202→    let attempt = 0;
203→    const stepMatch = entry.name.match(stepPattern);
204→    if (stepMatch) {
205→      stepNumber = Number(stepMatch[1]);
206→      attempt = Number(stepMatch[2]);
207→    } else if (genericPattern.test(entry.name) && typeof parsed.step_number === "number") {
208→      stepNumber = parsed.step_number;
209→      attempt = typeof parsed.step_attempt === "number" ? parsed.step_attempt : 0;
210→    }
211→    if (typeof stepNumber !== "number" || Number.isNaN(stepNumber) || Number.isNaN(attempt)) continue;
212→
213→    const model = extractModelNameFromDebug(parsed);
214→    if (!model) continue;
215→    const existing = byStep.get(stepNumber);
216→    if (!existing || attempt >= existing.attempt) {
217→      byStep.set(stepNumber, { model, attempt });
218→    }
219→  }
220→
221→  const out = {};
222→  for (const [step, value] of byStep.entries()) {
223→    out[String(step)] = value.model;
224→  }
225→  return out;
226→}
227→
228→async function readLogText(projectId, runId, kind, lines = 400) {
229→  const files = LOG_KIND_TO_FILES[kind];
230→  if (!files || files.length === 0) return "";
231→  const runPath = safeJoin(runsRoot, projectId, runId);
232→  const candidates = await Promise.all(
233→    files.map(async (file) => {
234→      const logPath = path.join(runPath, file);
235→      const stat = await fs.stat(logPath).catch(() => null);
236→      return stat ? { logPath, mtimeMs: stat.mtimeMs } : null;
237→    })
238→  );
239→  const selected = candidates
240→    .filter(Boolean)
241→    .sort((a, b) => b.mtimeMs - a.mtimeMs)[0];
242→  if (!selected) return "";
243→  const logPath = selected.logPath;
244→  const raw = await fs.readFile(logPath, "utf-8").catch(() => "");
245→  const allLines = raw.split("\n");
246→  return allLines.slice(Math.max(0, allLines.length - lines)).join("\n");
247→}
248→
249→async function listFiles(projectId, runId, root = "") {
250→  const runPath = safeJoin(runsRoot, projectId, runId);
251→  const start = safeJoin(runPath, root || ".");
252→  const out = [];
253→
254→  async function walk(current) {
255→    const ents = await fs.readdir(current, { withFileTypes: true }).catch(() => []);
256→    for (const ent of ents) {
257→      const abs = path.join(current, ent.name);
258→      const rel = path.relative(runPath, abs).replace(/\\/g, "/");
259→      if (ent.isDirectory()) {
260→        if (rel.startsWith(".git")) continue;
261→        await walk(abs);
262→      } else {
263→        const stat = await fs.stat(abs).catch(() => null);
264→        if (!stat) continue;
265→        out.push({
266→          path: rel,
267→          size: stat.size,
268→          mtime_ms: stat.mtimeMs,
269→        });
270→        if (out.length >= 1000) return;
271→      }
272→    }
273→  }
274→
275→  await walk(start);
276→  out.sort((a, b) => b.mtime_ms - a.mtime_ms);
277→  return out;
278→}
279→
280→async function serveStatic(res, pathname, rootDir = publicDir) {
281→  const filePath = pathname === "/" ? path.join(rootDir, "index.html") : safeJoin(rootDir, pathname.slice(1));
282→  const contentType = filePath.endsWith(".css")
283→    ? "text/css; charset=utf-8"
284→    : filePath.endsWith(".js")
285→      ? "application/javascript; charset=utf-8"
286→      : "text/html; charset=utf-8";
287→  const body = await fs.readFile(filePath, "utf-8").catch(() => null);
288→  if (body === null) {
289→    sendText(res, 404, "Not found");
290→    return;
291→  }
292→  res.writeHead(200, { "Content-Type": contentType });
293→  res.end(body);
294→}
295→
296→const server = http.createServer(async (req, res) => {
297→  const reqUrl = new URL(req.url ?? "/", `http://${req.headers.host ?? "localhost"}`);
298→  const pathname = reqUrl.pathname;
299→
300→  try {
301→    if (pathname === "/v2" || pathname.startsWith("/v2/")) {
302→      const v2Path = pathname === "/v2" ? "/" : pathname.slice(3);
303→      if (v2Path === "/run") {
304→        await serveStatic(res, "/run.html", publicV2Dir);
305→        return;
306→      }
307→      await serveStatic(res, v2Path, publicV2Dir);
308→      return;
309→    }
310→
311→    if (pathname === "/v3" || pathname.startsWith("/v3/")) {
312→      const v3Path = pathname === "/v3" ? "/" : pathname.slice(3);
313→      if (v3Path === "/run") {
314→        await serveStatic(res, "/run.html", publicV3Dir);
315→        return;
316→      }
317→      await serveStatic(res, v3Path, publicV3Dir);
318→      return;
319→    }
320→
321→    if (pathname === "/api/runs") {
322→      const runs = await listRuns();
323→      sendJson(res, 200, { runs, runs_root: runsRoot });
324→      return;
325→    }
326→
327→    if (pathname === "/api/run") {
328→      const project = reqUrl.searchParams.get("project");
329→      const run = reqUrl.searchParams.get("run");
330→      if (!project || !run) {
331→        sendJson(res, 400, { error: "Missing project/run query params" });
332→        return;
333→      }
334→      const data = await loadRun(project, run);
335→      sendJson(res, 200, data);
336→      return;
337→    }
338→
339→    if (pathname === "/api/events") {
340→      const project = reqUrl.searchParams.get("project");
341→      const run = reqUrl.searchParams.get("run");
342→      const limit = Number(reqUrl.searchParams.get("limit") ?? "300");
343→      if (!project || !run) {
344→        sendJson(res, 400, { error: "Missing project/run query params" });
345→        return;
346→      }
347→      const runPath = safeJoin(runsRoot, project, run);
348→      const events = await readEvents(runPath);
349→      sendJson(res, 200, { events: events.slice(Math.max(0, events.length - limit)) });
350→      return;
351→    }
352→
353→    if (pathname === "/api/log") {
354→      const project = reqUrl.searchParams.get("project");
355→      const run = reqUrl.searchParams.get("run");
356→      const kind = reqUrl.searchParams.get("kind");
357→      const lines = Number(reqUrl.searchParams.get("lines") ?? "400");
358→      if (!project || !run || !kind) {
359→        sendJson(res, 400, { error: "Missing project/run/kind query params" });
360→        return;
361→      }
362→      const text = await readLogText(project, run, kind, lines);
363→      sendText(res, 200, text);
364→      return;
365→    }
366→
367→    if (pathname === "/api/files") {
368→      const project = reqUrl.searchParams.get("project");
369→      const run = reqUrl.searchParams.get("run");
370→      const root = reqUrl.searchParams.get("root") ?? "";
371→      if (!project || !run) {
372→        sendJson(res, 400, { error: "Missing project/run query params" });
373→        return;
374→      }
375→      const files = await listFiles(project, run, root);
376→      sendJson(res, 200, { files });
377→      return;
378→    }
379→
380→    await serveStatic(res, pathname);
381→  } catch (err) {
382→    sendJson(res, 500, {
383→      error: err instanceof Error ? err.message : String(err),
384→    });
385→  }
386→});
