198→function buildStepMeta(runData, events) {
199→  const byStep = new Map();
200→  for (const step of runData?.steps ?? []) {
201→    byStep.set(step.step_number, {
202→      startedAt: step.started_at || null,
203→      completedAt: step.completed_at || null,
204→      model: runData?.step_models?.[String(step.step_number)] || "",
205→      errors: [],
206→      outputs: new Set(),
207→    });
208→  }
209→
210→  for (const evt of events) {
211→    const stepNum = evt?.data?.step;
212→    if (typeof stepNum !== "number") continue;
213→    if (!byStep.has(stepNum)) {
214→      byStep.set(stepNum, { startedAt: null, completedAt: null, model: "", errors: [], outputs: new Set() });
215→    }
216→    const meta = byStep.get(stepNum);
217→    if (evt.event_type === "step.started") meta.startedAt = evt.timestamp || meta.startedAt;
218→    if (evt.event_type === "step.completed" || evt.event_type === "step.failed") meta.completedAt = evt.timestamp || meta.completedAt;
219→
220→    const model = inferModel(evt.data);
221→    if (model) meta.model = model;
222→
223→    const outputs = []
224→      .concat(derivePaths(evt?.data?.files))
225→      .concat(derivePaths(evt?.data?.paths))
226→      .concat(derivePaths(evt?.data?.artifacts))
227→      .concat(derivePaths(evt?.data?.changed_files));
228→    outputs.forEach((o) => meta.outputs.add(o));
229→
230→    if (String(evt.event_type).includes("failed") || String(evt.event_type).includes("error")) {
231→      meta.errors.push(String(evt?.data?.error || evt?.data?.reason || evt?.data?.message || evt.event_type));
232→    }
233→  }
234→
235→  return byStep;
236→}
