331→          const time = pickString(item, ["timestamp", "time", "created_at"]);
332→          return `
333→            <article class="planner-item">
334→              <div class="planner-top">
335→                <span>${escapeHtml(plannerFriendlyLabel(item))}</span>
336→                <span class="mono">${escapeHtml(time || `#${index + 1}`)}</span>
337→              </div>
338→              <p>${escapeHtml(shortText(plannerSummary(item), 180))}</p>
339→              <details data-detail-id="${escapeHtml(detailId)}" ${state.expandedPlanDetails.has(detailId) ? "open" : ""}>
340→                <summary>Raw JSON</summary>
341→                <pre id="${escapeHtml(id)}">${escapeHtml(JSON.stringify(item, null, 2))}</pre>
342→              </details>
343→            </article>
344→          `;
345→        })
346→        .join("")}
347→    </div>
348→  `;
349→}
