351→function dependencyCount(planStep) {
352→  const inputs = planStep?.context_inputs;
353→  if (!Array.isArray(inputs)) return 0;
354→  return inputs.filter((item) => item?.type === "step_output").length;
355→}
356→
357→function outputsCount(meta) {
358→  return Array.from(meta?.outputs ?? []).length;
359→}
360→
361→function renderSteps(runData, stepMeta) {
362→  const planByStep = new Map((runData.plan?.steps ?? []).map((s) => [s.step_number, s]));
363→  const steps = runData.steps ?? [];
364→  if (!steps.length) {
365→    stepsGrid.innerHTML = '<div class="empty">No steps found.</div>';
366→    return;
367→  }
368→
369→  stepsGrid.innerHTML = steps
370→    .map((step) => {
371→      const meta = stepMeta.get(step.step_number) || { startedAt: null, completedAt: null, model: "", errors: [], outputs: new Set() };
372→      const started = meta.startedAt || step.started_at;
373→      const completed = meta.completedAt || step.completed_at;
374→      const ran = durationBetween(started, completed);
375→      const planStep = planByStep.get(step.step_number);
376→      const model = meta.model || planStep?.model || "-";
377→      const depCount = dependencyCount(planStep);
378→      const outCount = outputsCount(meta);
379→      const errCount = (meta.errors ?? []).length;
380→      const selected = state.selectedStep === step.step_number ? "selected" : "";
381→
382→      return `
383→        <article class="step-card ${escapeHtml(step.status || "pending")} ${selected}" data-step="${escapeHtml(String(step.step_number))}">
384→          <header>
385→            <span class="step-status ${escapeHtml(step.status || "pending")}"></span>
386→            <strong>Step ${escapeHtml(String(step.step_number))} · ${escapeHtml(step.agent || "agent")}</strong>
387→            <span class="model-chip">${escapeHtml(model)}</span>
388→          </header>
389→          <p class="task">${escapeHtml(step.task || "-")}</p>
390→          <div class="chips-row">
391→            <span class="metric">Started ${escapeHtml(fmtDate(started))}</span>
392→            <span class="metric">Ran ${escapeHtml(fmtDuration(ran))}</span>
393→            <span class="metric">${escapeHtml(humanTokens(step.tokens))}</span>
394→          </div>
395→          <div class="chips-row">
396→            <span class="pill">Outputs ${escapeHtml(String(outCount))}</span>
397→            <span class="pill">Dependencies ${escapeHtml(String(depCount))}</span>
398→            <span class="pill error">Errors ${escapeHtml(String(errCount))}</span>
399→          </div>
400→        </article>
401→      `;
402→    })
403→    .join("");
404→}
