406→function eventPreview(event) {
407→  return (
408→    pickString(event?.data, ["message", "error", "reason", "task"]) ||
409→    textFromContent(event?.data?.content) ||
410→    textFromContent(event?.data?.output) ||
411→    "No payload"
412→  );
413→}
