415→function renderTimeline(events, runData) {
416→  const ascending = events.slice(-180);
417→  if (!ascending.length) {
418→    timelineList.innerHTML = '<li class="empty">No events</li>';
419→    return;
420→  }
421→
422→  timelineList.innerHTML = ascending
423→    .map((evt) => {
424→      const stepNum = evt?.data?.step;
425→      const step = typeof stepNum === "number" ? (runData.steps ?? []).find((s) => s.step_number === stepNum) : null;
426→      const context = typeof stepNum === "number" ? `step ${stepNum}${step?.agent ? ` · ${step.agent}` : ""}` : "system";
427→      return `
428→        <li class="timeline-item">
429→          <span class="timeline-dot"></span>
430→          <div class="timeline-content">
431→            <div class="timeline-head">
432→              <strong>${escapeHtml(stepHeading(evt, runData))}</strong>
433→              <span>${escapeHtml(context)}</span>
434→            </div>
435→            <p>${escapeHtml(shortText(eventPreview(evt), 160))}</p>
436→            <code>${escapeHtml(fmtIso(evt.timestamp))}</code>
437→          </div>
438→        </li>
439→      `;
440→    })
441→    .join("");
442→}
443→
444→function classifyAgentItem(item) {
445→  const nested = item?.item ?? {};
446→  const kind = pickString(nested, ["type"]) || pickString(item, ["type", "event_type"]);
447→  const command = pickString(nested, ["command", "cmd"]) || pickString(item, ["command", "cmd"]);
448→  const thought =
449→    pickString(nested, ["prompt", "instructions", "input"]) ||
450→    pickString(item, ["prompt", "instructions", "input"]) ||
451→    textFromContent(nested.input) ||
452→    textFromContent(item.input);
453→  const message =
454→    pickString(nested, ["text", "message", "assistant_message", "output_text"]) ||
455→    pickString(item, ["message", "assistant_message", "output_text"]) ||
456→    textFromContent(nested.output) ||
457→    textFromContent(item.output);
458→
459→  const lower = String(kind).toLowerCase();
460→  if (lower === "command_execution" || command) {
461→    return { title: "Command executed", preview: shortText(command || message || thought, 130), code: true };
462→  }
463→  if (lower === "agent_message" || lower === "message") {
464→    return { title: "Agent message", preview: shortText(message || thought, 130), code: false };
465→  }
466→  if (lower === "thought" || lower === "reasoning") {
467→    return { title: "Thought", preview: shortText(thought || message, 130), code: false };
468→  }
469→  if (lower.includes("thread.started")) return { title: "Session started", preview: "", code: false };
470→  if (lower.includes("turn.started")) return { title: "Turn started", preview: "", code: false };
471→  if (lower.includes("item.completed")) return { title: "Item completed", preview: shortText(message || command || thought, 130), code: false };
472→  return { title: "Event", preview: shortText(message || command || thought, 130), code: false };
473→}
474→
475→function renderAgentOut(raw, stepNumber) {
476→  const lines = String(raw || "")
477→    .split("\n")
478→    .map((line) => line.trim())
479→    .filter(Boolean);
480→  if (!lines.length) return '<div class="empty">No output</div>';
481→
482→  const parsed = lines.map((line) => parseJsonSafe(line)).filter((item) => item && typeof item === "object");
483→  const candidates = parsed.length ? parsed : [];
484→  const filtered = candidates.filter((item) => (typeof stepNumber === "number" ? getStepNumber(item) === stepNumber : true));
485→  const target = filtered.length ? filtered : candidates;
486→
487→  if (!target.length) return '<div class="empty">No matching output</div>';
488→
489→  return target
490→    .slice(-140)
491→    .map((item, index) => {
492→      const cls = classifyAgentItem(item);
493→      const step = getStepNumber(item);
494→      const id =
495→        pickString(item?.item, ["id"]) ||
496→        pickString(item, ["id", "event_id"]) ||
497→        `${pickString(item, ["type", "event_type"]) || "row"}-${step || "x"}-${index}`;
498→      const blockId = `log-json-${id}`;
499→      const expanded = state.expandedLogIds.has(blockId);
500→      return `
501→        <article class="log-item">
502→          <button class="log-row" type="button" data-json-id="${escapeHtml(blockId)}">
503→            <span class="title">${escapeHtml(cls.title)}</span>
504→            <span class="meta">#${index + 1}${step ? ` · step ${step}` : ""}</span>
505→          </button>
506→          ${cls.preview ? `<p class="preview ${cls.code ? "code" : ""}">${escapeHtml(cls.preview)}</p>` : ""}
507→          <pre id="${escapeHtml(blockId)}" class="json-block" ${expanded ? "" : "hidden"}>${escapeHtml(JSON.stringify(item, null, 2))}</pre>
508→        </article>
509→      `;
510→    })
511→    .join("");
512→}
513→
514→function groupErrByStep(raw) {
515→  const lines = String(raw || "")
516→    .split("\n")
517→    .map((line) => line.trim())
518→    .filter(Boolean);
519→  const map = new Map();
520→
521→  for (const line of lines) {
522→    const parsed = parseJsonSafe(line);
523→    let step = null;
524→    let text = line;
525→    if (parsed) {
526→      step = getStepNumber(parsed);
527→      text =
528→        pickString(parsed, ["error", "message", "reason"]) ||
529→        pickString(parsed?.data, ["error", "message", "reason"]) ||
530→        textFromContent(parsed?.content) ||
531→        line;
532→    }
533→    const key = typeof step === "number" ? `step ${step}` : "unscoped";
534→    if (!map.has(key)) map.set(key, []);
535→    map.get(key).push(text);
536→  }
537→
538→  return map;
539→}
540→
541→function renderAgentErr(raw, stepNumber) {
542→  const grouped = groupErrByStep(raw);
543→  const sections = Array.from(grouped.entries()).filter(([key]) => {
544→    if (typeof stepNumber !== "number") return true;
545→    return key === `step ${stepNumber}`;
546→  });
547→
548→  if (!sections.length) return '<div class="empty">No errors</div>';
549→
550→  return sections
551→    .map(
552→      ([group, messages]) => `
553→      <section class="err-group">
554→        <h4>${escapeHtml(group)}</h4>
555→        ${messages.slice(-30).map((m) => `<p>${escapeHtml(shortText(m, 240))}</p>`).join("")}
556→      </section>
557→    `
558→    )
559→    .join("");
560→}
561→
562→function renderValueTree(value, key = null, depth = 0) {
563→  const keyHtml = key !== null ? `<span class="json-key">${escapeHtml(String(key))}</span>: ` : "";
564→
565→  if (value === null) return `<div class="tree-row">${keyHtml}<span class="json-null">null</span></div>`;
566→  if (typeof value === "string") return `<div class="tree-row">${keyHtml}<span class="json-string">"${escapeHtml(value)}"</span></div>`;
567→  if (typeof value === "number") return `<div class="tree-row">${keyHtml}<span class="json-number">${value}</span></div>`;
568→  if (typeof value === "boolean") return `<div class="tree-row">${keyHtml}<span class="json-bool">${value}</span></div>`;
569→
570→  if (Array.isArray(value)) {
571→    if (!value.length) return `<div class="tree-row">${keyHtml}<span>[]</span></div>`;
572→    return `
573→      <details class="tree-node" ${depth < 2 ? "open" : ""}>
574→        <summary>${keyHtml}[${value.length}]</summary>
575→        <div class="tree-children">
576→          ${value.map((item, i) => renderValueTree(item, i, depth + 1)).join("")}
577→        </div>
578→      </details>
579→    `;
580→  }
581→
582→  const entries = Object.entries(value);
583→  if (!entries.length) return `<div class="tree-row">${keyHtml}<span>{}</span></div>`;
584→  return `
585→    <details class="tree-node" ${depth < 2 ? "open" : ""}>
586→      <summary>${keyHtml}{${entries.length}}</summary>
587→      <div class="tree-children">
588→        ${entries.map(([k, v]) => renderValueTree(v, k, depth + 1)).join("")}
589→      </div>
590→    </details>
591→  `;
592→}
593→
594→function findStepPayload(value, stepNumber) {
595→  if (!value || typeof value !== "object") return null;
596→  if (Array.isArray(value)) {
597→    for (const item of value) {
598→      const found = findStepPayload(item, stepNumber);
599→      if (found) return found;
600→    }
601→    return null;
602→  }
603→  if (value.step === stepNumber || value.step_number === stepNumber) return value;
604→  for (const child of Object.values(value)) {
605→    const found = findStepPayload(child, stepNumber);
606→    if (found) return found;
607→  }
608→  return null;
609→}
610→
611→function renderResult(raw, stepNumber) {
612→  const parsed = parseJsonSafe(raw);
613→  if (!parsed) return `<pre class="json-block">${escapeHtml(raw || "(empty)")}</pre>`;
614→  const scoped = typeof stepNumber === "number" ? findStepPayload(parsed, stepNumber) || parsed : parsed;
615→  return `<div class="json-tree">${renderValueTree(scoped)}</div>`;
616→}
617→
618→function errorCountFromErr(raw) {
619→  return String(raw || "")
620→    .split("\n")
621→    .map((line) => line.trim())
622→    .filter(Boolean).length;
623→}
624→
625→function updateTabBadges(errRaw) {
626→  const errCount = errorCountFromErr(errRaw);
627→  for (const tab of logTabs) {
628→    const kind = tab.dataset.kind;
629→    if (kind === "agent_stderr") {
630→      tab.textContent = errCount > 0 ? `Agent Err (${errCount})` : "Agent Err";
631→    } else if (kind === "agent_stdout") {
632→      tab.textContent = "Agent Out";
633→    } else if (kind === "agent_result") {
634→      tab.textContent = "Result JSON";
635→    }
636→  }
637→}
638→
639→async function loadLog(kind = state.logKind) {
640→  state.logKind = kind;
641→  for (const tab of logTabs) tab.classList.toggle("active", tab.dataset.kind === kind);
642→
643→  const raw = await fetchText(
644→    `/api/log?project=${encodeURIComponent(project)}&run=${encodeURIComponent(run)}&kind=${encodeURIComponent(kind)}&lines=1400`
645→  ).catch(() => "");
646→
647→  if (kind === "agent_stdout") {
648→    logView.innerHTML = renderAgentOut(raw, state.selectedStep);
649→    return;
650→  }
651→  if (kind === "agent_stderr") {
652→    logView.innerHTML = renderAgentErr(raw, state.selectedStep);
653→    return;
654→  }
655→  logView.innerHTML = renderResult(raw, state.selectedStep);
656→}
657→
658→async function refresh() {
659→  if (!project || !run) {
660→    statusLine.textContent = "Missing project/run query params.";
661→    return;
662→  }
663→
664→  try {
665→    const [runData, eventsRes, plannerStdout, plannerStderr, agentErrRaw] = await Promise.all([
666→      fetchJson(`/api/run?project=${encodeURIComponent(project)}&run=${encodeURIComponent(run)}`),
667→      fetchJson(`/api/events?project=${encodeURIComponent(project)}&run=${encodeURIComponent(run)}&limit=800`),
668→      fetchText(`/api/log?project=${encodeURIComponent(project)}&run=${encodeURIComponent(run)}&kind=planner_stdout&lines=1000`).catch(() => ""),
669→      fetchText(`/api/log?project=${encodeURIComponent(project)}&run=${encodeURIComponent(run)}&kind=planner_stderr&lines=400`).catch(() => ""),
670→      fetchText(`/api/log?project=${encodeURIComponent(project)}&run=${encodeURIComponent(run)}&kind=agent_stderr&lines=1400`).catch(() => ""),
671→    ]);
672→
673→    state.runData = runData;
674→    state.events = Array.isArray(eventsRes.events) ? eventsRes.events : [];
675→    state.plannerStdout = plannerStdout;
676→    state.plannerStderr = plannerStderr;
677→    state.stepMeta = buildStepMeta(runData, state.events);
678→
679→    renderSummary(runData);
680→    renderPlanPanel(runData, state.events);
681→    renderSteps(runData, state.stepMeta);
682→    renderTimeline(state.events, runData);
683→    updateTabBadges(agentErrRaw);
684→
685→    await loadLog(state.logKind);
686→
687→    statusLine.textContent = `Loaded ${project}/${run}`;
688→    lastRefreshed.textContent = `Last refreshed: ${new Date().toLocaleTimeString()}`;
689→  } catch (error) {
690→    statusLine.textContent = `Error: ${error instanceof Error ? error.message : String(error)}`;
691→  }
692→}
