694→stepsGrid.addEventListener("click", async (event) => {
695→  const card = event.target.closest(".step-card");
696→  if (!card) return;
697→  const step = Number(card.dataset.step);
698→  if (!Number.isFinite(step)) return;
699→  state.selectedStep = state.selectedStep === step ? null : step;
700→  if (state.runData) renderSteps(state.runData, state.stepMeta);
701→  await loadLog(state.logKind);
702→});
