718→  if (hidden) {
719→    planBody.removeAttribute("hidden");
720→    planToggle.setAttribute("aria-expanded", "true");
721→  } else {
722→    planBody.setAttribute("hidden", "");
723→    planToggle.setAttribute("aria-expanded", "false");
724→  }
